/*
 * Unlike other property loading funciton, this file is NOT generated by some python code.
 * This is due to the fact that in any case there is no need to load the atomic density
 * matrices for all elements and all basis sets. Y. Z. 02/24/23
 */

#include "chemcache/chemcache.hpp"
#include <chemist/chemist.hpp>
#include "nwx_atomic_densities.hpp"

namespace chemcache {

    void load_atom_dm(chemist::PeriodicTable::size_type Z, std::string basis_name, chemist::PeriodicTable& pt) {
        pt.add_atom_dm(Z, basis_name, chemcache::detail_::get_atomic_density_(basis_name, Z));

    } // function load_atom_dm

} // namespace chemcache